/* Code tidied up by ScrapBook */
@import url("load.php");
.ArticlePreviewInner h1, .ArticlePreviewInner h2, .ArticlePreviewInner h3, .ArticlePreviewInner h4, .ArticlePreviewInner h5, .ArticlePreviewInner h6, .WikiaArticle h1, .WikiaArticle h2, .WikiaArticle h3, .WikiaArticle h4, .WikiaArticle h5, .WikiaArticle h6, .WikiaPage h1, .WikiaPage h2, .WikiaPage h3, .WikiaPage h4, .WikiaPage h5, .WikiaPage h6, #bodyContent h1, #bodyContent h2, #bodyContent h3, #bodyContent h4, #bodyContent h5, #bodyContent h6 { color: rgb(255, 217, 66); }
a { color: rgb(102, 187, 255); }
a:visited { color: rgb(64, 112, 208); }
table.grey { border: 1px solid rgb(102, 102, 102); background-color: rgb(34, 34, 34); color: rgb(255, 255, 255); }
table.grey tr th, table.grey thead tr th { color: rgb(255, 255, 255); background: rgb(0, 0, 0) url("MA_page_bar.png") repeat-x scroll 0% 0%; text-align: left; font-weight: bold; }
table.grey td { border: 1px outset rgb(136, 136, 136); }
div.notice { background-color: rgb(64, 31, 34); border: 1px dashed rgb(204, 0, 0); color: rgb(255, 255, 255); margin: 2em 10% 1em; padding: 0.5em 1em; vertical-align: middle; text-align: justify; font-size: smaller; }
div.notice p, div.header-notice p, div.warning p, div.message p { line-height: 120% !important; }
div#ma-article-type { border-bottom: 1px solid rgb(68, 68, 68); background-position: left top; text-align: right; padding-top: 1em; padding-right: 1em; min-height: 55px; max-height: 55px; font-size: large; font-variant: small-caps; }
div.type-mirror { background: rgb(0, 0, 0) url("Articletype_mirror.png") no-repeat scroll 0% 0%; }
@media print {
  *, body, h1, h2, h3, h4, h5, h6, .WikiaPage, .WikiaArticle { color: rgb(0, 0, 0); background-color: rgb(255, 255, 255); }
}
div.type-mirror { background: rgb(0, 0, 0) url("Articletype_mirror.png") no-repeat scroll 0% 0%; }
small { font-size: 80%; }
a { text-decoration: none; }
i { font-style: italic; }
.WikiaPage, .WikiaPageBackground { background: rgb(46, 46, 51) none repeat scroll 0px 0px !important; }
.WikiaPage { border-bottom: 1px solid rgb(102, 102, 102); border-left: 1px solid rgb(102, 102, 102); border-right: 1px solid rgb(102, 102, 102); }
.WikiaArticle h2 { margin-top: 0.5em; }
.WikiaArticle .toc { background-color: rgb(34, 34, 34); border: 1px solid rgb(102, 102, 102); border-collapse: separate; }
.article-categories { background-color: rgb(34, 34, 34) !important; background-image: none !important; border: 1px solid rgb(102, 102, 102); }
.tleft { margin: 0px 10px 0px 0px !important; outline: medium none !important; }
.tright { margin: 0px 0px 0px 10px !important; outline: medium none !important; }
.article-thumb { background-color: rgb(34, 34, 34); border: 1px solid rgb(102, 102, 102); padding: 3px 5px 0px 3px; }
.article-thumb a { display: inline; }
.article-thumb img { background-color: rgb(21, 21, 21); border: 1px solid rgb(64, 64, 64); }
.article-thumb .caption { color: rgb(255, 255, 255) !important; }
.WikiaHeader .wikia-button, .wikia-chiclet-button, a.wikia-button, .wikia-single-button a, .wikia-menu-button, input[type="submit"], input[type="reset"], input[type="button"], .button, button { background-color: rgb(71, 70, 70); background-image: linear-gradient(to bottom, rgb(109, 109, 109) 35%, rgb(71, 70, 70) 65%); border: 1px solid rgb(46, 46, 51); color: white; }
.WikiaHeader .wikia-button:hover, .wikia-chiclet-button:hover, a.wikia-button:hover, .wikia-single-button a:hover, .wikia-menu-button:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover, .button:hover, button:hover { background-color: rgb(61, 60, 60); background-image: linear-gradient(to bottom, rgb(99, 99, 99) 35%, rgb(61, 60, 60) 65%); }
a.wikia-button.secondary, .wikia-single-button a.secondary, .wikia-menu-button.secondary, input.secondary[type="submit"], input.secondary[type="reset"], input.secondary[type="button"], .button.secondary, button.secondary { background-color: rgb(32, 32, 32); background-image: linear-gradient(to bottom, rgb(34, 34, 34) 35%, rgb(32, 32, 32) 65%); border: 1px solid rgb(64, 64, 64); color: rgb(213, 212, 212); }
a.wikia-button.secondary:hover, .wikia-single-button a.secondary:hover, .wikia-menu-button.secondary:hover, input.secondary[type="submit"]:hover, input.secondary[type="reset"]:hover, input.secondary[type="button"]:hover, .button.secondary:hover, button.secondary:hover { background-color: rgb(22, 22, 22); background-image: linear-gradient(to bottom, rgb(24, 24, 24) 35%, rgb(22, 22, 22) 65%); }
.WikiaArticle { color: white; background: transparent none repeat scroll 0px 0px !important; border: medium none; box-shadow: none; }
#WikiaArticle a[href^="https://"]::after, #WikiaArticle a[href*="https%3A%2F%2F"]::after { padding: 0px 3px 0px 0px; background-image: url("link_secure.gif"); }
#WikiaArticle a[href*="wikipedia"]::after { padding: 0px 3px 0px 0px; background-image: url("Wikipedia.png"); }
#WikiaArticle div.noicon a::after, #WikiaArticle span.noicon a::after { background-image: none !important; padding: 0px !important; width: auto; }
.WikiaPage .page-header__title { color: rgb(255, 217, 66); }
