/* Code tidied up by ScrapBook */
#xfce-header { background: rgb(51, 51, 51) url("data:image/gif;base64,") no-repeat scroll 3px 50%; box-shadow: 0px 0px 8px rgba(0, 0, 0, 0.5); font-family: "Helvetica Neue Light",HelveticaNeue-Light,"Helvetica Neue",Helvetica,Arial,sans-serif; font-size: 14px; height: 35px; }
#xfce-header ul { list-style: outside none none; float: right; margin: 5px; padding: 0px; }
#xfce-header ul li { float: left; margin: 0px; }
#xfce-header a { color: rgb(153, 153, 153); text-decoration: none; display: block; padding: 0px 8px; border: 0px none; line-height: 25px; transition: color 0.2s linear 0s; }
#xfce-header ul li a:hover { color: rgb(255, 255, 255); text-decoration: underline; }
#xfce-header-title { display: none; }
#xfce-header-clear { clear: both; }
#xfce-header-subtitle { display: none; }
body, h1, h2, h3, h4, ol, ul, li { margin: 0px; padding: 0px; font-weight: normal; font-style: normal; }
body { font-family: "Bitstream Vera Sans","Lucida Grande","Lucida Sans Unicode",Helvetica,Arial,Verdana,sans-serif; font-size: 80%; line-height: 1.5em; text-align: left; background-color: rgb(238, 238, 238); color: rgb(0, 0, 0); }
h1, h2, h3, h4, dl dt { color: rgb(51, 51, 51); }
h1 { font-size: 180%; font-weight: normal; margin: 0.5em 0px 1em; }
h2, h3, h4 { font-weight: bold; margin: 1em 0px; }
h2 { font-size: 125%; }
a, a:link, a:visited { color: rgb(0, 102, 204); text-decoration: none; outline: medium none; }
a:hover { color: rgb(255, 0, 0); text-decoration: underline; }
a span { color: rgb(51, 51, 51); }
a:hover span { color: rgb(255, 0, 0); }
hr { border-width: 1px medium medium; border-style: solid none none; border-color: rgba(0, 0, 0, 0.2) currentcolor currentcolor; -moz-border-top-colors: none; -moz-border-right-colors: none; -moz-border-bottom-colors: none; -moz-border-left-colors: none; border-image: none; margin: 2em 0px; }
ul { list-style-type: square; }
input[type="submit"], button, select { border-radius: 0.2em; border: 1px solid rgba(0, 0, 0, 0.2); background: rgb(247, 247, 247) linear-gradient(to bottom, rgba(255, 255, 255, 0.5), rgba(0, 0, 0, 0.02) 1px, rgba(0, 0, 0, 0.05)) repeat-x scroll 0% 0%; color: rgb(0, 0, 0); padding: 0.2em 0.5em; outline: medium none ! important; transition: all 150ms ease 0s; }
select { -moz-appearance: none; }
input[type="submit"]:hover, button:hover, select:hover { background-color: rgb(255, 255, 255); color: rgb(0, 102, 204); text-decoration: none; }
input, input[type="text"], textarea { background-color: rgb(247, 247, 247); color: rgb(0, 0, 0); border-radius: 0.2em; border: 1px solid rgba(0, 0, 0, 0.2); padding: 0.2em 0.5em; }
.main_width, #main { width: 95%; min-width: 830px; max-width: 1400px; margin: 0px auto; padding: 0px 0.5em; }
#content { display: flex; background-color: rgb(255, 255, 255); box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.25); border-radius: 4px; }
#article { -moz-box-flex: 70; flex: 70 1 0%; padding: 2em; border-right: 1px solid rgb(204, 204, 204); line-height: 1.7em; }
#related { -moz-box-flex: 30; flex: 30 1 0%; max-width: 230px; padding: 1em 1.5em; }
#related:empty { display: none; }
#related h2 { margin: 1em 0px 0.5em; font-weight: normal; }
#related ul { margin: 0px 0px 0px 2em; }
#related ul li { margin-bottom: 0.5em; }
#related ul li a { display: block; }
.group::after { content: "."; display: block; height: 0px; clear: both; visibility: hidden; }
a span { color: inherit; }
#header .breadcrumbs { float: left; }
#header .tools { float: right; margin-top: -0.4em; }
.mobileTools { display: none; }
@media screen and (max-width: 850px) {
  #header .tools div, #header .tools select, #header .tools form, .mobileTools { display: inline; }
}
.dokuwiki div.page { box-shadow: none; border: medium none; border-top-right-radius: 0px; border-bottom-right-radius: 0px; margin-bottom: 0px; }
.dokuwiki #related ul { margin-left: 1em; }
#header, #footer { padding: 0.4em 0px; }
#header { padding-top: 0.8em; }
#footer .bars { display: flex; margin-bottom: 0.4em; }
#footer .bars div { flex: 1 1 0%; }
#footer .bar-left { text-align: left; }
#footer .bar-right { text-align: right; }
#footer .bar-left form, #footer .bar-right form { display: inline; }
pre.code { background-color: rgba(0, 0, 0, 0.04); border: 1px solid rgba(0, 0, 0, 0.1); border-radius: 0.3em; padding: 0.3em 0.8em; }
@media screen and (max-width: 850px) {
  h1 { margin-top: 0.3em; font-size: 160%; text-align: left; }
  .main_width, #main { min-width: 300px; }
  #content { display: block; }
  #article { border: medium none; padding: 1.5em; }
  #related { background-color: rgb(250, 250, 250); border-top: 1px solid rgb(238, 238, 238); max-width: 100%; padding-bottom: 1.5em; border-bottom-right-radius: 4px; border-bottom-left-radius: 4px; }
}
